CREATE OR REPLACE FUNCTION hubviews."fn_unionInfo"()
 RETURNS TABLE("ID" integer, "AssociationID" integer, "ClientKey" integer, "Lang" character, "Name" character varying, "AssociationName" character varying, "Mobile" character varying, "AlternateMobile" character varying, "RegistrationNumber" character varying, "AdditonalInformation" text, "LocationGranularity" character varying, "LocationKey" json, "StateKey" integer, "State" text, "DistrictKey" integer, "District" text, "MandalKey" integer, "Mandal" text, "VillageKey" integer, "Village" text, "Address" text, "Tags" text[], "Members" json, "IsActive" boolean, "CreatedOn" timestamp without time zone, "CreatedBy" character varying, "ModifiedOn" timestamp without time zone, "ModifiedBy" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
    RETURN QUERY
    SELECT 
        a."ID",
        a."AssociationID",
        a."ClientKey",
        a."Lang",
        a."Name",
        a."AssociationName",
        a."Mobile",
        a."AlternateMobile",
        a."RegistrationNumber",
        a."AdditonalInformation",
        a."LocationGranularity",
        a."LocationKey"::text::json AS "LocationKey",
        a."StateKey",
        a."State",
        a."DistrictKey",
        a."District",
        a."MandalKey",
        a."Mandal",
        a."VillageKey",
        a."Village",
        a."Address",
        string_to_array(string_agg(
            CASE
                WHEN b."Tags"::text = 'None'::text THEN ''::character varying
                ELSE b."Tags"
            END::text, ','::text), ','::text) AS "Tags",
        a."Members"::text::json AS "Members",
        a."IsActive",
        a."CreatedOn",
        a."CreatedBy",
        a."ModifiedOn",
        a."ModifiedBy"
    FROM (
        SELECT 
            "AssociationInfo"."ID",
            "AssociationInfo"."ID" AS "AssociationID",
            "AssociationInfo"."ClientKey",
            "AssociationInfo"."Lang",
            "AssociationInfo"."Name",
            "AssociationInfo"."Name" AS "AssociationName",
            "AssociationInfo"."Mobile",
            "AssociationInfo"."AlternateMobile",
            "AssociationInfo"."RegistrationNumber",
            "AssociationInfo"."AdditonalInformation",
            "AssociationInfo"."LocationGranularity",
            "AssociationInfo"."LocationKey",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'StateKey'::text) ->> 'ID'::text)::integer AS "StateKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'StateKey'::text) ->> 'Value'::text AS "State",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'DistrictKey'::text) ->> 'ID'::text)::integer AS "DistrictKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'DistrictKey'::text) ->> 'Value'::text AS "District",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'MandalKey'::text) ->> 'ID'::text)::integer AS "MandalKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'MandalKey'::text) ->> 'Value'::text AS "Mandal",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'VillageKey'::text) ->> 'ID'::text)::integer AS "VillageKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'VillageKey'::text) ->> 'Value'::text AS "Village",
            "AssociationInfo"."Address",
            unnest("AssociationInfo"."Tags") AS "TagID",
            "AssociationInfo"."Members",
            "AssociationInfo"."IsActive",
            "AssociationInfo"."CreatedOn",
            "AssociationInfo"."CreatedBy",
            "AssociationInfo"."ModifiedOn",
            "AssociationInfo"."ModifiedBy"
        FROM connecthub."AssociationInfo"
    ) a
    LEFT JOIN masters."TagList" b 
    ON a."TagID" = b."ID"::text 
    AND a."ClientKey" = b."ClientKey" 
    AND a."Lang" = b."Lang"
    GROUP BY 
        a."ID", 
        a."AssociationID", 
        a."ClientKey", 
        a."Lang", 
        a."Name", 
        a."AssociationName", 
        a."Mobile", 
        a."AlternateMobile", 
        a."RegistrationNumber", 
        a."AdditonalInformation", 
        a."LocationGranularity", 
        (a."LocationKey"::text), 
        a."StateKey", 
        a."State", 
        a."DistrictKey", 
        a."District", 
        a."MandalKey", 
        a."Mandal", 
        a."VillageKey", 
        a."Village", 
        a."Address", 
        (a."Members"::text), 
        a."IsActive", 
        a."CreatedOn", 
        a."CreatedBy", 
        a."ModifiedOn", 
        a."ModifiedBy";
END;
$function$
;






CREATE OR REPLACE FUNCTION hubviews."fn_unionInformation"()
 RETURNS TABLE("ID" integer, "AssociationID" integer, "ClientKey" integer, "Lang" character, "Name" character varying, "AssociationName" character varying, "Mobile" character varying, "AlternateMobile" character varying, "RegistrationNumber" character varying, "AdditonalInformation" text, "LocationGranularity" character varying, "LocationKey" json, "StateKey" integer, "State" text, "DistrictKey" integer, "District" text, "MandalKey" integer, "Mandal" text, "VillageKey" integer, "Village" text, "Address" text, "Tags" json, "Members" json, "IsActive" boolean, "CreatedOn" timestamp without time zone, "CreatedBy" character varying, "ModifiedOn" timestamp without time zone, "ModifiedBy" character varying)
 LANGUAGE plpgsql
AS $function$
BEGIN
    RETURN QUERY
    SELECT 
        a."ID",
        a."AssociationID",
        a."ClientKey",
        a."Lang",
        a."Name",
        a."AssociationName",
        a."Mobile",
        a."AlternateMobile",
        a."RegistrationNumber",
        a."AdditonalInformation",
        a."LocationGranularity",
        COALESCE(a."LocationKey"::text::json, '[]'::json) AS "LocationKey",
        a."StateKey",
        a."State",
        a."DistrictKey",
        a."District",
        a."MandalKey",
        a."Mandal",
        a."VillageKey",
        a."Village",
        a."Address",
        CASE
            WHEN json_agg(
                CASE
                    WHEN b."ID" = 0 THEN '{}'::json
                    ELSE json_build_object('ID', b."ID", 'Value', b."Tags")
                END)::text = '[{}]'::text THEN '[]'::json
            ELSE json_agg(
                CASE
                    WHEN b."ID" = 0 THEN '{}'::json
                    ELSE json_build_object('ID', b."ID", 'Value', b."Tags")
                END)
        END AS "Tags",
        COALESCE(a."Members"::text::json, '[]'::json) AS "Members",
        a."IsActive",
        a."CreatedOn",
        a."CreatedBy",
        a."ModifiedOn",
        a."ModifiedBy"
    FROM (
        SELECT 
            "AssociationInfo"."ID",
            "AssociationInfo"."ID" AS "AssociationID",
            "AssociationInfo"."ClientKey",
            "AssociationInfo"."Lang",
            "AssociationInfo"."Name",
            "AssociationInfo"."Name" AS "AssociationName",
            "AssociationInfo"."Mobile",
            "AssociationInfo"."AlternateMobile",
            "AssociationInfo"."RegistrationNumber",
            "AssociationInfo"."AdditonalInformation",
            "AssociationInfo"."LocationGranularity",
            "AssociationInfo"."LocationKey",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'StateKey'::text) ->> 'ID'::text)::integer AS "StateKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'StateKey'::text) ->> 'Value'::text AS "State",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'DistrictKey'::text) ->> 'ID'::text)::integer AS "DistrictKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'DistrictKey'::text) ->> 'Value'::text AS "District",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'MandalKey'::text) ->> 'ID'::text)::integer AS "MandalKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'MandalKey'::text) ->> 'Value'::text AS "Mandal",
            ((("AssociationInfo"."LocationKey" -> 0) -> 'VillageKey'::text) ->> 'ID'::text)::integer AS "VillageKey",
            (("AssociationInfo"."LocationKey" -> 0) -> 'VillageKey'::text) ->> 'Value'::text AS "Village",
            "AssociationInfo"."Address",
            unnest("AssociationInfo"."Tags") AS "TagID",
            "AssociationInfo"."Members",
            "AssociationInfo"."IsActive",
            "AssociationInfo"."CreatedOn",
            "AssociationInfo"."CreatedBy",
            "AssociationInfo"."ModifiedOn",
            "AssociationInfo"."ModifiedBy"
        FROM connecthub."AssociationInfo"
    ) a
    LEFT JOIN masters."TagList" b 
    ON a."TagID" = b."ID"::text 
    AND a."ClientKey" = b."ClientKey" 
    AND a."Lang" = b."Lang"
    GROUP BY 
        a."ID", 
        a."AssociationID", 
        a."ClientKey", 
        a."Lang", 
        a."Name", 
        a."AssociationName", 
        a."Mobile", 
        a."AlternateMobile", 
        a."RegistrationNumber", 
        a."AdditonalInformation", 
        a."LocationGranularity", 
        (a."LocationKey"::text), 
        a."StateKey", 
        a."State", 
        a."DistrictKey", 
        a."District", 
        a."MandalKey", 
        a."Mandal", 
        a."VillageKey", 
        a."Village", 
        a."Address", 
        (a."Members"::text), 
        a."IsActive", 
        a."CreatedOn", 
        a."CreatedBy", 
        a."ModifiedOn", 
        a."ModifiedBy";
END;
$function$
;



