BEGIN
    -- Location filter logic
    IF _ACKey IS NULL AND _MandalKey IS NULL AND _VillageKey IS NULL THEN 
        _locfilterstring = '';
        _mlocfilterstring = '';
    ELSIF _ACKey IS NOT NULL AND _MandalKey IS NULL AND _VillageKey IS NULL THEN
--        _locfilterstring = ' AND a."ACKey" = ' || _ACKey||' ';
        _locfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey||' ';
        _mlocfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey||' ';
