--        _locfilterstring = ' AND a."ACKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey||' ';
	    _locfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey||' ';
        _mlocfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey||' ';
