--        _locfilterstring = ' AND a."ACKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey || ' AND "VillageKey" = ' || _VillageKey ||' ';
       _locfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey || ' AND "VillageKey" = ' || _VillageKey ||' ';
        _mlocfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey || ' AND "VillageKey" = ' || _VillageKey ||' ';
    END IF;

    -- Filter type logic
    IF _FilterType = 'Mobile' THEN 
		_Mobilefilter = ' AND (trim("Mobile") = ' || quote_literal(_Value) || ' OR trim("AlternateMobile") = ' || quote_literal(_Value) || ')';
        _mMobilefilter = ' AND (trim("Mobile") = ' || quote_literal(_Value) || ' OR trim("AlternateMobile") = ' || quote_literal(_Value) || ')';
