CREATE OR REPLACE FUNCTION connecthub."GrievanceTemplateInfo"(_clientkey integer, _lang character varying, _userid character varying)
 RETURNS json
 LANGUAGE plpgsql
AS $function$
DECLARE
    _return JSON;

--exception variables
    _state   TEXT;
    _msg     TEXT;
    _detail  TEXT;
    _hint    TEXT;
    _context TEXT;

BEGIN
    SELECT json_agg(row_to_json(a))
    INTO _return
    FROM (
        SELECT 
            "ID", 
            "Name", 
            "IssueType", 
            json_build_object('ID', "DepartmentID", 'Value', "Department") AS "GrievanceType", 
            "Priority", 
            "GrievanceText"
        FROM connecthub."GrievanceTemplates"
        WHERE "ClientKey" = _clientkey 
          AND "Lang" = _lang
    ) a;


_return:= json_build_object('Status','Success','Details',_return);
	
	
return _return;

exception when others then 

    get stacked diagnostics
        _state   = returned_sqlstate,
        _msg     = message_text,
        _detail  = pg_exception_detail,
        _hint    = pg_exception_hint,
        _context = pg_exception_context;
       
            
     insert into connecthub."EXCEPTION_LOG" ("procedure", "state","msg", "detail", "hint", "context")
     values('GrievanceTemplateInfo', _state, _msg, _detail, _hint, _context);
    
      
	_return = json_build_object('Status','Failed','Details', json_build_object('State',_state, '_msg', _msg , '_detail', _detail, '_hint', _detail, '_context', _context));

	return _return;

END;
