INSERT INTO connecthub."GrievanceType" ("ID", "ClientKey", "Lang", "GrievanceType", "Description", "IsActive", "CreatedOn", "CreatedBy", "ModifiedOn", "ModifiedBy", "SubjectRef", "HOD", "HODKey", "Department", "DepartmentKey") VALUES(51, 2, 'en', 'Support Services for Women, Children, and Disabled', 'The content outlines various departments and subjects related to the welfare of women, children, disabled individuals, and senior citizens. It highlights specific issues and complaints regarding the quality and availability of aids and appliances, hostel accommodations, child protection services, and Anganwadi center operations. The document emphasizes the need for improvements in service delivery, addressing grievances related to inadequate facilities, delays in applications, and the quality of resources provided to beneficiaries.', true, '2024-09-18 19:24:03.558', NULL, NULL, NULL, false, 'Others', NULL, 'Others', NULL);
