INSERT INTO connecthub."GrievanceType" ("ID", "ClientKey", "Lang", "GrievanceType", "Description", "IsActive", "CreatedOn", "CreatedBy", "ModifiedOn", "ModifiedBy", "SubjectRef", "HOD", "HODKey", "Department", "DepartmentKey") VALUES(59, 2, 'en', 'Disaster Management Relief Measures Overview', 'This document outlines the various subjects and sub-subjects related to disaster management, specifically focusing on relief measures for natural calamities such as cyclones, floods, tsunamis, droughts, and other notified disasters. It details the types of assistance provided, including clothing and household goods for affected families, ex-gratia payments for injuries and deaths, support for damaged housing, and subsidies for agriculture, horticulture, and fisheries. The aim is to address the immediate needs and recovery efforts for those impacted by such disasters.', true, '2024-09-18 19:24:03.558', NULL, NULL, NULL, false, 'Others', NULL, 'Others', NULL);
INSERT INTO connecthub."GrievanceType" ("ID", "ClientKey", "Lang", "GrievanceType", "Description", "IsActive", "CreatedOn", "CreatedBy", "ModifiedOn", "ModifiedBy", "SubjectRef", "HOD", "HODKey", "Department", "DepartmentKey") VALUES(60, 2, 'en', 'Overview of Transport and Road Management Issues', 'The content outlines the responsibilities and subjects related to the Transport, Roads, and Buildings department, specifically focusing on various entities such as the APSRTC and NHAI. It highlights key issues concerning bus operations, national highways, and road infrastructure, as well as administrative matters. Additionally, it addresses concerns related to driving licenses and vehicle registrations, detailing specific sub-issues like delays, incorrect details, and application cancellations.', true, '2024-09-18 19:24:03.558', NULL, NULL, NULL, false, 'Others', NULL, 'Others', NULL);
