CREATE OR REPLACE FUNCTION connecthub."ProcCrudGrievanceType"(_clientkey integer, _lang character, _userid character varying, _operation text, _grievancetype character varying, _description text)
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
-- variable declaration
	_return json;
	_List json;
	_ID int;
