--   		from connecthub."GrievanceType" where "ClientKey" = _ClientKey and "IsActive" = true and "Lang" = _Lang and "SubjectRef" = True
	
		select json_agg(distinct jsonb_build_object('ID', "HODKey", 'Value', "HOD") ) 
