CREATE OR REPLACE FUNCTION connecthub."GenerateUniqueTaskID"(_clientkey integer, lang character, _userkey text)
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
    _current_date TEXT;
    _ShortCode char(3);
    sequence_value INT;
    _interval int;
    _maxdate date;
BEGIN
    -- Get the ShortCode for the client
    SELECT "ClientShotCode" INTO _ShortCode 
    FROM connecthub."ClientInfo" 
    WHERE "ClientKey" = _ClientKey;

    -- Get the most recent task date for the given client and language
    SELECT max("CreatedOn")::date INTO _maxdate 
    FROM connecthub."Task" 
    WHERE "ClientKey" = _clientkey AND "Lang" = lang;

    -- Check if the most recent task was created today
    IF _maxdate = current_date THEN
        -- Get the current date in 'YYMMDD' format
        _current_date := to_char(CURRENT_DATE, 'YYMMDD');
        
        -- Get the next value of the sequence for task ID
        sequence_value := nextval('connecthub."SeqTaskID"');
    
    ELSIF _maxdate < current_date OR _maxdate IS NULL THEN
        -- If no task exists or the most recent task is from a previous day, reset the sequence
        SELECT setval('connecthub."SeqTaskID"', 1, false) INTO _interval;
        
        -- Get the current date in 'YYMMDD' format
        _current_date := to_char(CURRENT_DATE, 'YYMMDD');
        
        -- Get the next value of the sequence for task ID
        sequence_value := nextval('connecthub."SeqTaskID"');
    END IF;

    -- Return the concatenated unique TaskID
    RETURN 'TSK-'|| _ShortCode|| _current_date || lpad(sequence_value::text, 4, '0');
END;
$function$
;



