			_ClientKey:=(_input->'global'->>'ClientKey')::int, 
			_Lang:=(_input->'global'->>'Lang'), 
			_PageKey:=(_input->'params'->>'PageKey')::int 
			);
		
	elsif _input->>'requestURI' = 'RequestorSearch' then 
		_proc_name = 'ProcSearchRequestorInfo';
