			_userid:=(_input->'params'->>'UserID') ,
			_Type:=(_input->'params'->>'Type'),
			_Value:=(_input->'params'->>'Value'),
			_FilterID:=(_input->'params'->>'FilterID')::int 
			);
			
	elsif _input->>'requestURI' = 'Navigation' then 
		raise notice '%','mohan';
		_proc_name = 'ProcAfterLoggedIn';
		select * 
		into  _return 
		from connecthub."ProcAfterLoggedIn"(
			_ClientKey:=(_input->'global'->>'ClientKey')::int, 
