CREATE OR REPLACE FUNCTION connecthub."ProcGetPAList"(_clientkey integer, _lang character, _operation text, _requestid text, _assignee uuid, _userid text)
 RETURNS json
 LANGUAGE plpgsql
AS $function$
declare 
--variable declaration
	_return json;
	_id text;

--exception variables
    _state   TEXT;
    _msg     TEXT;
    _detail  TEXT;
    _hint    TEXT;
    _context TEXT;

begin 
	
if _operation = 'Get' then 
	
	select json_agg(row_to_json(a)  ) into _return from ( 
	select "UserKey" as "ID","UserName" "Value" from connecthub."UserInfo" ui 
	where "UserKey" in (select "UserKey" from connecthub."UserProfiles" up where "ProfileKey" = 2  
	and "ClientKey"= _clientkey) and "IsActive" = True and "ClientKey"= _clientkey
	) a;

	_return = json_build_object('Status','Success', 'Details', _return);

elsif _operation = 'Update' then 

	update connecthub."GrievanceInfo" rr 
	set "AssignedTo" = _assignee::text,
	"ModifiedBy" = _userid::text,
	"ModifiedUserName" = (SELECT "UserName" FROM connecthub."UserInfo" WHERE "UserKey" = _userid::text AND "ClientKey" = _ClientKey)
	where "ClientKey"= _clientkey and "GrievanceKey" = _requestid
	returning "GrievanceKey" into _id;
	
	_return = json_build_object('Status','Success', 'Details', json_build_object('List',json_build_object('RecordID',_id)));
--	_return = json_build_object('Status','Success', 'Details',_id);

end if;
	
	

	return _return;	

	exception when others then 

    get stacked diagnostics
        _state   = returned_sqlstate,
        _msg     = message_text,
        _detail  = pg_exception_detail,
        _hint    = pg_exception_hint,
        _context = pg_exception_context;
       
     insert into connecthub."EXCEPTION_LOG" ("procedure", "state","msg", "detail", "hint", "context")
     values('ProcGetAssigneesList', _state, _msg, _detail, _hint, _context);
    
	_return = json_build_object('Status','Failed','Details', json_build_object('State',_state, '_msg', _msg , '_detail', _detail, '_hint', _detail, '_context', _context));
--  
	return _return;
	
end;
$function$
;


CREATE OR REPLACE FUNCTION connecthub."ProcCrudRequestInformation"(_clientkey integer, _lang character, _operation character varying, _userid text, _grievancetype integer, _grievancetext text, _attachments text, _requestedfor json, _requestedby json, _referedby json, _tags text, _additionalinfo json, _grievancestatus integer, _remarks text, _createdby character varying, _modifiedby character varying, _offset integer, _limit integer, _id character varying, _role integer, _priority character varying, _islocationspecific boolean, _locationgranularity character varying, _locationkey json, _summarizedtext text, _grievanceby character varying, _audioattachment text, _subsubjectcode integer, _partycadrestatus character varying, _partycadredetails json)
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
-- variable declaration
	_return json;
	_List Record;
	_GID varchar(60);
	_query text;
	 r record;
	_Cnt int;
	_condition text;
	_reqFor json;
	_reqBy json;
	_refBy json;
	_sla int;
	_members json;
	_gr_district_loc record;
	_gr_location_from_requestor json;
--exception variables
    _state   TEXT;
    _msg     TEXT;
    _detail  TEXT;
    _hint    TEXT;
    _context TEXT;
begin

	select "SLA" into _sla from hubviews.vw_priority where lower("Priority") = lower(_priority);
	
   	if _Operation = 'Insert' then 
   		
   		if _ID is null then 
	   		select * into _GID from connecthub."GenerateUniqueGrievanceKey"(_ClientKey,_lang,_UserID);
	   	end if;
		
   		select 
		json_agg(json_build_object('ID',a."ID", 'Type', a."Type" ,'data',coalesce(b."obj", c."obj")))
		into _reqfor
		from 
		(select (json_array_elements(_RequestedFor)->>'ID')::int "ID", 
		(json_array_elements(_RequestedFor)->>'Type') "Type" ) as a
		left join 
				(select "RequestorID", a."ClientKey", a."Lang", row_to_json(a)::jsonb||jsonb_build_object('Type','Individual')||jsonb_build_object('OccupationID', b."ID" ,'Occupation', b."Occupation") obj 
		from connecthub."RequestorInformation" as a 
		left join masters."OccupationList" as b on a."ClientKey" = b."ClientKey" and a."Occupation" = b."ID"
		) as b on b."ClientKey" = _ClientKey and  b."Lang" = _Lang and a."ID" = b."RequestorID" and a."Type" = 'Individual'
		left join 
		(select "ID", "ClientKey", "Lang",row_to_json(a)::jsonb||jsonb_build_object('Type','Association') obj 
--		from hubviews."vw_unionInfo" as a) as c on c."ClientKey" = _ClientKey and  c."Lang" = _Lang and a."ID" = c."ID" and a."Type" = 'Association';
		from hubviews."fn_unionInfo"() as a) as c on c."ClientKey" = _ClientKey and  c."Lang" = _Lang and a."ID" = c."ID" and a."Type" = 'Association';
	
		select 
		json_agg(json_build_object('ID',a."ID", 'Type', a."Type" ,'data',coalesce(b."obj", c."obj")))
