	where "UserKey" = p_AssignedTo::text
	and "ProfileKey" = 1
	and "ClientKey" = p_ClientKey;

	if _isMLA = 1 then
	
	select json_agg(taskobj) filter (where "SNO" >p_offset and "SNO" <=p_offset+p_limit ) into _return from 
    (SELECT 
