    left join connecthub."UserInfo" crt on crt."UserKey" = t."CreatedBy" and crt."ClientKey" = t."ClientKey"
    WHERE t."ClientKey" = p_ClientKey
--      and t."AssignedTo" = p_AssignedTo
	and "mark_for_deletion" = false
