BEGIN
    -- Handle INSERT (Task Created)
    IF (TG_OP = 'INSERT') THEN
        INSERT INTO connecthub."TaskHistory"("TaskId", "ClientKey", "Lang", "TaskStatus", 
            activitytext, createdby, "CreatedOn", "ActionType",new_value)
        VALUES (NEW."TaskId", NEW."ClientKey", NEW."Lang", NEW."TaskStatus", 
            'Task Created', NEW."CreatedBy", NEW."CreatedOn", 'CREATE', 
           json_build_object(
            	'TaskId',NEW."TaskId", 
            	'Title',OLD."Title", 
            	'Description',OLD."Description", 
            	'AssignedTo',OLD."AssignedTo", 
            	'DueDate',OLD."DueDate", 
            	'Attachments',OLD."Attachments", 
            	'Priority',OLD."Priority", 
            	'mark_for_deletion',OLD."mark_for_deletion", 
            	'ClientKey',NEW."ClientKey", 
            	'Lang',NEW."Lang", 
            	'TaskStatus',NEW."TaskStatus", 
            	'ActivityText','Task Updated', 
            	'CreaetdBy',OLD."CreatedBy", 
            	'LastModifiedBy',NEW."LastModifiedBy", 
            	'CreatedOn',OLD."CreatedOn",
            	'LastUpdatedOn',New."LastUpdatedOn"
            	)
            );
        RETURN NEW;
    -- Handle UPDATE (Task Updated)
    ELSIF (TG_OP = 'UPDATE') THEN
        INSERT INTO connecthub."TaskHistory"("TaskId", "ClientKey", "Lang", "TaskStatus", 
            activitytext, createdby, "CreatedOn", "LastUpdatedOn", "ActionType", 
            old_value, new_value)
        VALUES (NEW."TaskId", NEW."ClientKey", NEW."Lang", NEW."TaskStatus", 
            'Task Updated', NEW."LastModifiedBy", OLD."CreatedOn", NEW."LastUpdatedOn", 'UPDATE',
            json_build_object(
            	'TaskId',OLD."TaskId", 
            	'Title',OLD."Title", 
            	'Description',OLD."Description", 
            	'AssignedTo',OLD."AssignedTo", 
            	'DueDate',OLD."DueDate", 
            	'Attachments',OLD."Attachments", 
            	'Priority',OLD."Priority", 
            	'mark_for_deletion',OLD."mark_for_deletion", 
            	'ClientKey',OLD."ClientKey", 
            	'Lang',OLD."Lang", 
            	'TaskStatus',OLD."TaskStatus", 
            	'ActivityText','Task Updated', 
            	'CreaetdBy',OLD."CreatedBy", 
            	'LastModifiedBy',OLD."LastModifiedBy", 
            	'CreatedOn',OLD."CreatedOn",
            	'LastUpdatedOn',OLD."LastUpdatedOn"
            	), 
            json_build_object(
            	'TaskId',NEW."TaskId", 
            	'Title',NEW."Title", 
            	'Description',NEW."Description", 
            	'AssignedTo',NEW."AssignedTo", 
            	'DueDate',NEW."DueDate", 
            	'Attachments',NEW."Attachments", 
            	'Priority',NEW."Priority", 
            	'mark_for_deletion',NEW."mark_for_deletion", 
            	'ClientKey',NEW."ClientKey", 
            	'Lang',NEW."Lang", 
            	'TaskStatus',NEW."TaskStatus", 
            	'ActivityText','Task Updated', 
            	'CreaetdBy',NEW."CreatedBy", 
            	'LastModifiedBy',NEW."LastModifiedBy", 
            	'CreatedOn',NEW."CreatedOn",
            	'LastUpdatedOn',New."LastUpdatedOn"
            	)
            );  -- Example for tracking description change
        RETURN NEW;
    END IF;
END;
$function$
;

