CREATE OR REPLACE VIEW hubviews.databricks_gdataset_percentile
AS WITH total_tickets AS (
         SELECT count(*)::numeric AS ticket_total_count
           FROM connecthub."GrievanceInfo"
        ), percentile_values AS (
         SELECT percentile_cont(0.25::double precision) WITHIN GROUP (ORDER BY ((subquery_for_percentiles.ticket_vill_count / subquery_for_percentiles.ticket_total_count * 100::numeric)::double precision)) AS p25,
            percentile_cont(0.50::double precision) WITHIN GROUP (ORDER BY ((subquery_for_percentiles.ticket_vill_count / subquery_for_percentiles.ticket_total_count * 100::numeric)::double precision)) AS p50,
            percentile_cont(0.75::double precision) WITHIN GROUP (ORDER BY ((subquery_for_percentiles.ticket_vill_count / subquery_for_percentiles.ticket_total_count * 100::numeric)::double precision)) AS p75
           FROM ( SELECT count(*) OVER (PARTITION BY a_1.request_state, a_1.request_district, a_1.request_ac_name, a_1.request_mandal, a_1.request_village)::numeric AS ticket_vill_count,
                    ( SELECT count(*)::numeric AS count
                           FROM connecthub."GrievanceInfo"
                          WHERE "GrievanceInfo"."GrievanceStatus" <> 7) AS ticket_total_count
                   FROM hubviews.vw_grievancedataset a_1) subquery_for_percentiles
        )
 SELECT a."ClientKey",
    a.grievance_key,
    a.grievance_type,
    a.grievance_text,
    a.department,
    a.hod,
    a.subject,
    a.subsubject,
    a.gender,
    a.requestor_tags,
    a.priority,
    a.sla,
    a.grievance_status,
    a.age_in_days,
    a.age_category,
    a.created_date,
    a.location_granularity,
    a.request_state,
    a.request_district,
    a.request_ac_name,
    a.request_mandal,
    a.request_village,
    a.ac_drop_down,
    a.source,
    a.requestor,
    t.ticket_total_count,
    (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::numeric(5,2) AS percentage,
        CASE
            WHEN (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::double precision <= p.p25 THEN 'P25'::text
            WHEN (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::double precision <= p.p50 THEN 'P50'::text
            WHEN (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::double precision <= p.p75 THEN 'P75'::text
            ELSE 'Above P75'::text
        END AS percentile,
        CASE
            WHEN (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::double precision <= p.p25 THEN '0-25%'::text
            WHEN (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::double precision <= p.p50 THEN '25-50%'::text
            WHEN (a.ticket_vill_count / t.ticket_total_count * 100::numeric)::double precision <= p.p75 THEN '50-75%'::text
            ELSE '75% +'::text
        END AS bucket
   FROM ( SELECT a_1."ClientKey",
            a_1.grievance_key,
            a_1.grievance_type,
            a_1.grievance_text,
            COALESCE(a_1.department, 'None'::text) AS department,
            COALESCE(a_1.hod, 'None'::text) AS hod,
            COALESCE(a_1.subject, 'None'::text) AS subject,
            COALESCE(a_1.subsubject, 'None'::text) AS subsubject,
            unnest(a_1.gender) AS gender,
            replace(unnest(a_1.requestor_tags), '\'::text, ''::text) AS requestor_tags,
            a_1.priority,
            a_1.sla,
            a_1.grievance_status,
            a_1.age_in_days,
            a_1.age_category,
            a_1.created_on::date AS created_date,
            a_1.location_granularity,
            a_1.request_state,
            a_1.request_district,
            a_1.request_ac_name,
            a_1.request_mandal,
            a_1.request_village,
            a_1.source,
            b.requestor,
                CASE
                    WHEN a_1.request_ac_name::text = 'URAVAKONDA'::text THEN a_1.request_ac_name
                    ELSE 'Other'::character varying
                END AS ac_drop_down,
            count(*) OVER (PARTITION BY a_1.request_state, a_1.request_district, a_1.request_ac_name, a_1.request_mandal, a_1.request_village)::numeric AS ticket_vill_count
           FROM hubviews.vw_grievancedataset a_1
             LEFT JOIN ( SELECT a_2."GrievanceKey",
                    string_agg(a_2.requestor, ', '::text) AS requestor
                   FROM ( SELECT unnamed_subquery."GrievanceKey",
                                CASE
                                    WHEN unnamed_subquery."ReferenceType"::text = 'Association'::text THEN ((((((unnamed_subquery."ReferenceInfo"::json ->> 'Name'::text) || '('::text) || unnamed_subquery."ReferenceRelation"::text) || ')'::text) || '('::text) || unnamed_subquery."ReferenceType"::text) || ')'::text
                                    ELSE ((((((((unnamed_subquery."ReferenceInfo"::json ->> 'RequestorFirstName'::text) || ' '::text) || (unnamed_subquery."ReferenceInfo"::json ->> 'RequestorLastName'::text)) || '('::text) || unnamed_subquery."ReferenceRelation"::text) || ')'::text) || '('::text) || unnamed_subquery."ReferenceType"::text) || ')'::text
                                END AS requestor
                           FROM ( SELECT DISTINCT "RequestRequestorList"."GrievanceKey",
                                    "RequestRequestorList"."ReferenceType",
                                    "RequestRequestorList"."ReferenceInfo"::text AS "ReferenceInfo",
                                    "RequestRequestorList"."ReferenceRelation"
                                   FROM connecthub."RequestRequestorList") unnamed_subquery) a_2
                  GROUP BY a_2."GrievanceKey") b ON a_1.grievance_key::text = b."GrievanceKey"::text) a
     CROSS JOIN total_tickets t
     CROSS JOIN percentile_values p;
     
    
