CREATE OR REPLACE FUNCTION connecthub.fn_update_task_status(p_clientkey integer, p_taskid character varying, p_taskstatus character varying, p_modifiedby character varying)
 RETURNS json
 LANGUAGE plpgsql
AS $function$
declare 
	_return json;
	_TaskId  varchar(40);

--exception variables
    _state   TEXT;
    _msg     TEXT;
    _detail  TEXT;
    _hint    TEXT;
    _context TEXT;
BEGIN
   
	UPDATE connecthub."Task"
    SET "TaskStatus" = p_taskstatus, 
	"LastModifiedBy" = p_ModifiedBy,
	"LastUpdatedOn" = (CURRENT_TIMESTAMP AT TIME ZONE 'Asia/Kolkata')::TIMESTAMP(0)
    WHERE "TaskId" = p_TaskId and "ClientKey" = p_ClientKey returning "TaskId" into _TaskId;

   	if _TaskId is null then 
   		_return = json_build_object('Status','Failed', 'Details', json_build_object('msg','Task ID does not exits'));
   	else 
	    _return = json_build_object('Status','Success', 'Details', json_build_object('msg', 'Successfully Updated Task Status'));
	end if;

	return _return;

exception when others then 

    get stacked diagnostics
        _state   = returned_sqlstate,
        _msg     = message_text,
        _detail  = pg_exception_detail,
        _hint    = pg_exception_hint,
        _context = pg_exception_context;
       
            
     insert into connecthub."EXCEPTION_LOG" ("procedure", "state","msg", "detail", "hint", "context")
     values('fn_update_task_status', _state, _msg, _detail, _hint, _context);
    
     _return = json_build_object('Status','Failed','Details', json_build_object('State',_state, '_msg', _msg , '_detail', _detail, '_hint', _hint, '_context', _context));

	return _return;
	
END;
