					'CSS',"CSS"::json,
					'TagConfig',"TagConfig"::json,
					'ElementType',"ElementType",
					'Count',(
					case when "ElementType" = 'tab' then (select ("ProGetRequestCategoriesList"->>'TotalRecords')::int from connecthub."ProGetRequestCategoriesList"(_ClientKey, _Lang, _UserKey, "ElementKey", null ,null, 1,0, _advfilters))
					else 0 end
						)
					) order by "ElementKey") el
		from 
		(select 
		"ElementKey",case when "ElementKey" = 16 then _UserName when "ElementKey" = 18 then _Pname else "Label" end "Label","TagContent" ,"Description" ,"Icon","Image","Class","CSS"::text ,"TagConfig"::text,"ElementType",  "PropertyType"
--		from hubviews.vw_uielementslist where "ClientKey"=_ClientKey and "Lang"= _Lang and "IsPublic" = true and "IsActive" = true and "PageKey" = _PageKey
		from hubviews.fn_uielementslist() where "ClientKey"=_ClientKey and "Lang"= _Lang and "IsPublic" = true and "IsActive" = true and "PageKey" = _PageKey
		union
		select a."ElementKey", b."Label", b."TagContent", b."Description", b."Icon", b."Image", b."Class", b."CSS"::text, b."TagConfig"::text, b."ElementType", "PropertyType" from connecthub."AuthenticationElements" as a 
--		join hubviews.vw_uielementslist as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = _PageKey
		join hubviews.fn_uielementslist() as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = _PageKey
		where a."RoleKey" in ( 	 	 	
		select "RoleKey" from connecthub."Profileroles" where "ClientKey" = _ClientKey and "IsActive" = true 
		and "ProfileKey" in (
							select "ProfileKey" from connecthub."UserProfiles" where "ClientKey" = _ClientKey and "IsActive" = true and "UserKey" = _UserKey
							)
		) and a."PageKey" = _PageKey and a."View" = true) as a 
		group by "PropertyType") as a;
	
	select json_object_agg("PropertyType","el") 
		into _CommonElements
		from 
		(select "PropertyType",
			json_object_agg("ElementKey", json_build_object(
					'ElementKey',"ElementKey",
					'Label',"Label",
					'TagContent',"TagContent",
					'Description',"Description",
					'Icon',"Icon",
					'Image',"Image",
					'Class',"Class",
					'CSS',"CSS"::json,
					'TagConfig',"TagConfig"::json,
					'ElementType',"ElementType",
					'Count',(
					case when "ElementType" = 'tab' then (select ("ProGetRequestCategoriesList"->>'TotalCount')::int from connecthub."ProGetRequestCategoriesList"(_ClientKey, _Lang, _UserKey, "ElementKey", null ,null, 1,0, null::json))
					else 0 end
						)
					) order by "ElementKey") el
			
		from 
		(select distinct a."ElementKey", b."Label", b."TagContent", b."Description", b."Icon", b."Image", b."Class", b."CSS"::text, b."TagConfig"::text, b."ElementType", "PropertyType" from connecthub."AuthenticationElements" as a 
--		join hubviews.vw_uielementslist as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = 0 and b."PageGroupKey" = 3 and b."Lang" = _Lang and b."ElementType" = 'tab'
		join hubviews.fn_uielementslist() as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = 0 and b."PageGroupKey" = 3 and b."Lang" = _Lang and b."ElementType" = 'tab'
		where a."RoleKey" in (
		select "RoleKey" from connecthub."Profileroles" where "ClientKey" = _ClientKey and "IsActive" = true 
		and "ProfileKey" in (
							select "ProfileKey" from connecthub."UserProfiles" where "ClientKey" = _ClientKey and "IsActive" = true and "UserKey" = _UserKey
							)
		) and a."PageKey" = 0 and a."PageGroupKey" = 3 and a."View" = true) as a 
		group by "PropertyType") as a;
	
	drop table if exists tempactions;
	 create temp table tempactions("PropertyType" text, obj json);
    
     for r in 
     	select distinct a."ElementKey", b."MasterQuery", b."QueryOrder","ElementType"
                 from connecthub."AuthenticationElements" as a
--                 join hubviews.vw_uielementslist as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."PageGroupKey" = b."PageGroupKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = 0 and b."PageGroupKey" = 0 and b."Lang" = _Lang and b."PropertyType" = 'Action'
                 join hubviews.fn_uielementslist() as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."PageGroupKey" = b."PageGroupKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = 0 and b."PageGroupKey" = 0 and b."Lang" = _Lang and b."PropertyType" = 'Action'
                 where a."RoleKey" in (
                 select "RoleKey" from connecthub."Profileroles" where "ClientKey" = _ClientKey and "IsActive" = true
                 and "ProfileKey" in (
                                     select "ProfileKey" from connecthub."UserProfiles" where "ClientKey" = _ClientKey and "IsActive" = true and "UserKey" = _UserKey
                                     )
                 ) and a."PageKey" = 0 and a."PageGroupKey" = 0 and a."View" = true
     loop
         if r."ElementType" in ('dropdown','checkbox', 'radio') then
                filters = ' where "ClientKey" = '||_ClientKey;
                query = 'select json_agg(obj) from (select json_build_object(''ID'',"ID", ''Value'', "Value", ''Color'' , "Color", ''Relation'', "Relation",''Action'', "Action", ''Role'', "rolerelation" '||
                        ' ) obj from ('||coalesce(r."MasterQuery",'')||
                        filters||' order by '||coalesce(r."QueryOrder",'')||') as a) as a' ;
                execute query into _dropdown_json;
            else
                _dropdown_json ='{}'::json;
         end if;
        
        
         insert into tempactions
         	 select "PropertyType",
             json_object_agg("ElementKey", json_build_object(
                     'ElementKey',"ElementKey",
                     'Label',"Label",
                     'TagContent',"TagContent",
                     'Description',"Description",
                     'Icon',"Icon",
                     'Image',"Image",
                     'Class',"Class",
                     'CSS',"CSS"::json,
                     'TagConfig',"TagConfig"::json,
                     'ElementType',"ElementType",
                     'ListValues',_dropdown_json
                     ) order by "ElementKey") el
         from
         (select distinct a."ElementKey", b."Label", b."TagContent", b."Description", b."Icon", b."Image", b."Class", b."CSS"::text, b."TagConfig"::text, b."ElementType", "PropertyType" from connecthub."AuthenticationElements" as a
--         join hubviews.vw_uielementslist as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = 0 and b."PageGroupKey" = 0 and a."PageGroupKey" = b."PageGroupKey" and b."Lang" = _Lang
         join hubviews.fn_uielementslist() as b on a."ClientKey" = b."ClientKey" and a."PageKey" = b."PageKey" and a."ElementKey" = b."ElementKey" and b."PageKey" = 0 and b."PageGroupKey" = 0 and a."PageGroupKey" = b."PageGroupKey" and b."Lang" = _Lang
         where a."ElementKey" = r."ElementKey") 
         group by "PropertyType";
     end loop;
    
     select json_build_object("PropertyType", json_agg(obj::json)) into _Actions  from tempactions
    group by "PropertyType";
   
   select json_agg(row_to_json(a))
   into _status_list
   from
   (select "StatusKey" , "Status","Color","Relation","Action",rolerelation   from masters."Status" where "ClientKey" = _ClientKey and "StatusKey" <> 7) as a; 
  
  	select "RoleKey" into _rolekey from connecthub."Profileroles" where "ClientKey" = _ClientKey and "IsActive" = true
     and "ProfileKey" in 
     (
     	select "ProfileKey" from connecthub."UserProfiles" where "ClientKey" = _ClientKey and "IsActive" = true and "UserKey" = _UserKey
     );
	
  
    select json_agg(row_to_json(a)) into _AllPages
    from
  	(select 'Page' "PropertyType", p."PageKey" "Key", pl."Page" "Name", pl."Title" "Label" ,"TagConfig", "IsPublic", p."IsActive" "Active", 
	coalesce(rr."RoleKey",_rolekey) "RoleKey" , coalesce(rr."IsActive",false) "View" from
	(select * from connecthub."PageList" where "TagConfig" is not null and "PageKey"  <> 2) as p
	join connecthub."PageList_Lang" as pl on pl."ClientKey" = p."ClientKey" and pl."PageGroupKey" = p."PageGroupKey" and pl."PageKey" = p."PageKey"
	left join connecthub."PageRoleAccess"  as rr on p."ClientKey"= rr."ClientKey" and p."PageKey" = rr."PageKey" 
	and p."PageGroupKey" = rr."PageGroupKey" and rr."RoleKey" = _rolekey) as a;

	select json_agg(row_to_json(a)) into _AllActions
    from
  	(select 'Action' "PropertyType" ,a."ElementKey" "Key", "Label" "Name", "Label" , "TagConfig", "IsPublic", "IsActive" "Active",
	coalesce(rr."RoleKey",_rolekey) "RoleKey", coalesce(rr."View",false) "View" from 
	(select * from connecthub."UIElementsInfo" where "PropertyType" = 'Action') as a
	join connecthub."UIElementsInfo_Lang" as b on a."ClientKey"= b."ClientKey" and a."ElementKey" = b."ElementKey"
	left join connecthub."AuthenticationElements" as rr on a."ClientKey"= rr."ClientKey" 
	and a."ElementKey" = rr."ElementKey" and rr."RoleKey" = _rolekey) as a;

	select "Theme", "Palette" into _theme, _palette from hubviews.vw_clientinfo
	where "ClientKey" = _ClientKey and "Lang" = _Lang;

	select "ShowPartyCadreTab" into _spartycadretab from connecthub."ClientInfo"
	where "ClientKey" = _ClientKey;

   raise notice 'pages: %',_Pages;
	_return = json_build_object('Status','Success','Details',json_build_object('UserInfo',_UserInfo,'Pages',_Pages,'UIElements',_UiElements, 'Other', _CommonElements, 'Actions', _Actions->'Action', 'StatusList', _status_list, 'AllPages',_AllPages, 'AllActions', _AllActions,'Theme',_theme,'Palette',_palette, 'ShowPartyCadreTab', _spartycadretab));

	return _return;

	exception when others then 

    get stacked diagnostics
        _state   = returned_sqlstate,
        _msg     = message_text,
        _detail  = pg_exception_detail,
        _hint    = pg_exception_hint,
        _context = pg_exception_context;
       
     insert into connecthub."EXCEPTION_LOG" ("procedure", "state","msg", "detail", "hint", "context")
     values('ProcAfterLoggedIn', _state, _msg, _detail, _hint, _context);
    
	_return = json_build_object('Status','Failed','Details', json_build_object('State',_state, '_msg', _msg , '_detail', _detail, '_hint', _detail, '_context', _context));
--  
	return _return;

end; $function$
;




 DROP FUNCTION hubviews.fn_userinfo();

CREATE OR REPLACE FUNCTION hubviews.fn_userinfo()
 RETURNS TABLE("ClientKey" integer, "UserKey" character varying, "Email" character varying, "Lang" character varying, "FullName" character varying, "IsActive" boolean, "Contact" character varying, "Photo" text, "CreatedOn" timestamp without time zone)
 LANGUAGE plpgsql
AS $function$
BEGIN
    RETURN QUERY
    SELECT 
        a."ClientKey",
        a."UserKey"::varchar(70),
        a."Email"::varchar(70),
        b."Lang"::varchar(5),
        b.text::varchar(70) AS "FullName",
        a."IsActive",
        a."Contact",
        a."Photo",
        a."CreatedOn"
    FROM 
        connecthub."UserInfo" a
    JOIN 
        connecthub."UserInfo_Lang" b 
    ON 
        a."ClientKey" = b."ClientKey" 
        AND a."UserKey"::text = b."UserKey"::text
    ORDER BY 
        a."ClientKey", 
        a."UserKey";
END;
$function$
;



 DROP FUNCTION hubviews.fn_uielementslist();

CREATE OR REPLACE FUNCTION hubviews.fn_uielementslist()
 RETURNS TABLE("ClientKey" integer, "PageGroupKey" integer, "PageKey" integer, "ElementKey" integer, "Lang" character varying, "Label" character varying, "TagContent" character varying, "Description" character varying, "Icon" character varying, "Image" character varying, "Class" character varying, "CSS" json, "TagConfig" json, "ElementType" character varying, "IsPublic" boolean, "IsActive" boolean, "PropertyType" character varying, "MasterQuery" text, "QueryOrder" text, "MobileImage" text, "OrdinalPosition" integer)
 LANGUAGE plpgsql
AS $function$
BEGIN
    RETURN QUERY
    SELECT 
        a."ClientKey",
        a."PageGroupKey",
        a."PageKey",
        a."ElementKey",
        b."Lang",
        b."Label",
        b."TagContent",
        b."Description",
        a."Icon",
        a."Image",
        a."Class",
        a."CSS",
        a."TagConfig",
        a."ElementType",
        a."IsPublic",
        a."IsActive",
        a."PropertyType",
        a."MasterQuery",
        a."QueryOrder",
        a."MobileImage",
        a."OrdinalPosition"
    FROM connecthub."UIElementsInfo" a
    JOIN connecthub."UIElementsInfo_Lang" b 
        ON a."ClientKey" = b."ClientKey" 
        AND a."PageGroupKey" = b."PageGroupKey" 
        AND a."PageKey" = b."PageKey" 
        AND a."ElementKey" = b."ElementKey";
END;
$function$
;



 DROP FUNCTION connecthub.fn_homepage(int4, varchar, varchar, json);

CREATE OR REPLACE FUNCTION connecthub.fn_homepage(_clientkey integer, _lang character varying, _userid character varying, _parameterizedjson json)
 RETURNS json
 LANGUAGE plpgsql
AS $function$
DECLARE
    total_grievances json;
    total_draft_grievances json;
    grievances_for_correction json;
    grievances_created_by_me json;
    drafts json;
	grievances_for_inreview json;
	grievances_in_inreview json;
	_return json;
	grievance_templates json;
	grievances_for_correction_count int;
	grievances_created_by_me_count int;
	drafts_count int;
	grievances_in_inreview_count int;
	events_count_for_nextweek int;
	appointments_count_for_nextweek int;
	grievances_assigned_to_me json;
	grievances_assigned_to_me_count int;

    -- Parameters for filters
    sendforcorrections_limit integer;
    sendforcorrections_offset integer;
    drafts_limit integer;
    drafts_offset integer;
    inreview_limit int;
    inreview_offset int ;
    createdbyme_limit int;
    createdbyme_offset int;
	assignedtome_limit int;
	assignedtome_offset int;
  
BEGIN
    -- Extract parameters for filtering
    SELECT COALESCE((_parameterizedjson->'GrievancesForCorrection'->>'Limit')::integer, 10),
           COALESCE((_parameterizedjson->'GrievancesForCorrection'->>'Offset')::integer, 0)
    INTO sendforcorrections_limit, sendforcorrections_offset;

    SELECT COALESCE((_parameterizedjson->'Drafts'->>'Limit')::integer, 10),
           COALESCE((_parameterizedjson->'Drafts'->>'Offset')::integer, 0)
    INTO drafts_limit, drafts_offset;
   
    SELECT COALESCE((_parameterizedjson->'GrievancesInInReview'->>'Limit')::integer, 10),
           COALESCE((_parameterizedjson->'GrievancesInInReview'->>'Offset')::integer, 0)
    INTO inreview_limit, inreview_offset;
   
   SELECT COALESCE((_parameterizedjson->'GrievancesCreatedByMe'->>'Limit')::integer, 10),
           COALESCE((_parameterizedjson->'GrievancesCreatedByMe'->>'Offset')::integer, 0)
    INTO createdbyme_limit, createdbyme_offset;

	SELECT COALESCE((_parameterizedjson->'GrievancesAssignedToMe'->>'Limit')::integer, 10),
           COALESCE((_parameterizedjson->'GrievancesAssignedToMe'->>'Offset')::integer, 0)
    INTO assignedtome_limit, assignedtome_offset;
   
--   SELECT coalesce((_parameterizedjson->>'SearchTerm'),'')into _searchvalue;
   
--   raise notice '%,%',sendforcorrections_limit,sendforcorrections_offset;
   

    -- Query for Total Grievances
    SELECT json_build_object('TotalGrievances', count(*))
    INTO total_grievances
    FROM connecthub."GrievanceInfo" gi
    WHERE gi."CreatedBy" = _userid
    AND gi."ClientKey" = _clientkey
    AND gi."GrievanceStatus" <> 7
    AND gi."Source" = 'Meenestham';

    -- Query for Total Draft Grievances
    SELECT json_build_object('TotalDraftGrievances', count(*))
    INTO total_draft_grievances
    FROM connecthub."GrievanceInfo" gi
    WHERE gi."CreatedBy" = _userid
    AND gi."ClientKey" = _clientkey
    AND gi."GrievanceStatus" = 7
    AND gi."Source" = 'Meenestham';

    -- Query for Grievances Sent for Correction with Limit and Offset
	select 
		json_build_object('obj', json_agg(b)) 
	INTO grievances_for_correction
	from 
	(
    SELECT 
--	json_agg(
               json_build_object(
                   'GrievanceKey', "GrievanceKey",
				   'Department',"Department",
                   'Reason for Correction', "Note",
                   'Date: Received for Correction', TO_CHAR("ModifiedOn"::timestamp, 'YYYY-MM-DD HH:MI:SS AM')
               ) b
--           )
--    INTO grievances_for_correction
    FROM connecthub."GrievanceInfo" gi
    WHERE gi."CreatedBy" = _userid
    AND gi."ClientKey" = _clientkey
    AND gi."GrievanceStatus" = 0
    and ("GrievanceKey" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%' or "Note" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%' or "Department" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%')
    ORDER BY 
	  CASE 
	    WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'Date: Received for Correction' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'desc' THEN "ModifiedOn"::text
	    WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'GrievanceKey' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'desc' THEN "GrievanceKey" 
	    WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'Reason for Correction' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'desc' THEN "Note" 
		WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'Department' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'desc' THEN "Department"
	  end desc,
	   case
		WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'Date: Received for Correction' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'asc' THEN "ModifiedOn"::text
	    WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'GrievanceKey' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'asc' THEN "GrievanceKey" 
	    WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'Reason for Correction' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'asc' THEN "Note" 
		WHEN (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Field') = 'Department' and (_parameterizedjson->'GrievancesForCorrection'->'Sort'->>'Order') = 'asc' THEN "Department"
	  	end asc, "ModifiedOn"::timestamp desc
    limit sendforcorrections_limit offset sendforcorrections_offset
	) a;

	select count(*) from (
    select * into grievances_for_correction_count
    FROM connecthub."GrievanceInfo" gi
    WHERE gi."CreatedBy" = _userid
    AND gi."ClientKey" = _clientkey
    AND gi."GrievanceStatus" = 0
    and ("GrievanceKey" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%' or "Note" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%' or "Department" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%')) a;
--    ORDER BY "ModifiedOn" desc
--    limit sendforcorrections_limit offset sendforcorrections_offset

-- grievances created by me

WITH requestor_data AS (
        (SELECT "RequestorID",
            CONCAT("RequestorFirstName", ' ', "RequestorLastName") AS "RequestorName",
            "Mobile" AS "RequestorMobileNumber", 
            'Individual' AS "RequestorType"
        FROM connecthub."RequestorInformation" ri)
        UNION
        (SELECT "ID" AS "RequestorID",
            "Name" AS "RequestorName",
            "Mobile" AS "RequestorMobileNumber", 
            'Association' AS "RequestorType"
        FROM connecthub."AssociationInfo" ai)
    ), grivenace_data AS (
        SELECT "GrievanceKey", "Status","Department",
            TO_CHAR("CreatedOn"::timestamp, 'YYYY-MM-DD') AS "Created On",
            trim(replace((json_array_elements("RequestedBy"::json)->'ID')::text,'"',''))::integer AS "RequestorID", 
            trim(replace((json_array_elements("RequestedBy"::json)->'Type')::text,'"','')) AS "RequestorType"
        FROM connecthub."GrievanceInfo" gi
        WHERE gi."CreatedBy" = _userid
        AND gi."ClientKey" = _clientkey
        AND gi."GrievanceStatus" <> 7
        AND gi."Source" = 'Meenestham'
    ), formulate_griveance_with_requestor_information AS (
        SELECT t1.*, 
            CONCAT(t2."RequestorName", ' ( ', 
                CASE 
                    WHEN t2."RequestorMobileNumber" IS NULL THEN 'No Mobile Number' 
                    ELSE t2."RequestorMobileNumber" 
                END, ' )') AS "RequestorDeatails" 
        FROM grivenace_data t1
        LEFT OUTER JOIN requestor_data t2
            ON t1."RequestorID" = t2."RequestorID"
            AND t1."RequestorType" = t2."RequestorType"
    )
    SELECT 
	json_build_object('obj', json_agg(b)) 
	INTO grievances_created_by_me
	from 
	(
	select 
                json_build_object(
                    'GrievanceKey', "GrievanceKey",
					'Department',"Department",
                    'Created On', "Created On",
                    'Status', "Status",
--                    'RequstorDetails', details
					'RequestorDetails', details
                ) b
    FROM (

	select * from (
        SELECT "GrievanceKey","Department", "Created On", "Status", string_agg("RequestorDeatails", '<br/>') AS details
        FROM formulate_griveance_with_requestor_information
        GROUP BY "GrievanceKey","Department", "Created On", "Status"
	) a
	where ("GrievanceKey" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%' or details ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%' or "Department" ilike '%'||coalesce((_parameterizedjson->>'SearchTerm'),'')||'%')
--	ORDER BY 
--	  CASE 
--	    WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') IS NOT NULL THEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field')||' '||(_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order')
--	    ELSE '"Created On"::text' ||' desc'
--	  END
--        LIMIT createdbyme_limit OFFSET createdbyme_offset
	ORDER BY 
	  CASE 
	    WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'Created On' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'desc' THEN "Created On"::text
	    WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'GrievanceKey' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'desc' THEN "GrievanceKey" 
	    WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'RequstorDetails' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'desc' THEN "details" 
		WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'Department' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'desc' THEN "Department" 
	  end desc,
	   case
		WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'Created On' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'asc' THEN "Created On"::text
	    WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'GrievanceKey' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'asc' THEN "GrievanceKey" 
	    WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'RequstorDetails' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'asc' THEN "details" 
		WHEN (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Field') = 'Department' and (_parameterizedjson->'GrievancesCreatedByMe'->'Sort'->>'Order') = 'asc' THEN "Department"
	  	end asc, "Created On"::timestamp desc
        LIMIT createdbyme_limit OFFSET createdbyme_offset
) aa
    ) b;

   
   WITH requestor_data AS (
        (SELECT "RequestorID",
            CONCAT("RequestorFirstName", ' ', "RequestorLastName") AS "RequestorName",
            "Mobile" AS "RequestorMobileNumber", 
            'Individual' AS "RequestorType"
        FROM connecthub."RequestorInformation" ri)
        UNION
        (SELECT "ID" AS "RequestorID",
