CREATE OR REPLACE FUNCTION connecthub."ProcCrudRequestorInformation"(_clientkey integer, _lang character, _operation character varying, _userid text, _photo text, _requestortype integer, _requestorfirstname character varying, _requestorlastname character varying, _age integer, _gender character varying, _mobile character varying, _alternatemobile character varying, _email character varying, _occupation integer, _union text, _constituencytype character varying, _constituencykey integer, _mandalkey integer, _villagekey integer, _door_flatno character varying, _pincode character, _addressline1 text, _addressline2 text, _voterid character varying, _relationtype character varying, _relationname character varying, _relationcontact character varying, _relationalternatecontact character varying, _tags text, _requestoradditionalinfo json, _offset integer, _limit integer, _id character varying, _isreferedby boolean, _additionalinfo json, _caste character varying)
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
-- variable declaration
	_return json;
	_List Record;
	_RID int;
--exception variables
    _state   TEXT;
    _msg     TEXT;
    _detail  TEXT;
    _hint    TEXT;
    _context TEXT;
begin
	
	_occupation = coalesce(_occupation::text, '0');
	_tags = case when _tags = '' then '0' else coalesce(_tags, '0') end;
	
   	if _Operation = 'Insert' then 
   	

		if exists (select * from connecthub."RequestorInformation" where lower("RequestorFirstName") = lower(_RequestorFirstName) and lower("RequestorLastName") = lower(_RequestorLastName) and "Age" = _Age and lower("Gender") = lower(_Gender) and "Mobile" = _Mobile) then

			_return = json_build_object('Status','Fail', 'Details', 'Requestor already exists');

