			select * 
			into _List 
			from
			(select 
			"Source","RequestorID","Photo","RequestorType","RequestorTypeName","RequestorFirstName",
			"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email","OccupationID","Occupation",
