			"Union","UnionInfo"::text,"ConstituencyType","ConstituencyKey","ConstituencyName","MandalKey","Mandal",
			"VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2","RelationType",
			"RelationName","RelationContact","RelationAlternateContact","AdditionalInfo"::text,
			a."IsActive",a."CreatedBy",a."CreatedOn",a."ModifiedBY",a."ModifiedOn"
			, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed,"VoterID","Caste"
			) as a;
		
			_return = json_build_object('Status','Success', 'Details', json_build_object('List', _List));

