		"Door/FlatNo" = _Door_FlatNo,"PinCode" = _PinCode,
		"AddressLine1" = _AddressLine1,"AddressLine2" = _AddressLine2,"VoterID" = _VoterID,"RelationType" = _RelationType,
		"RelationName" = _RelationName,"RelationContact" = _RelationContact,"RelationAlternateContact" = _RelationAlternateContact,
		"Tags" = string_to_array(case when _Tags='' then '0' else _Tags end, ','),
