		"IsActive" = true,
		"CreatedBy" = _UserID,
		"ModifiedOn" = current_timestamp,
		"CreatedUserName" = (SELECT distinct "UserName" FROM connecthub."UserInfo" WHERE "UserKey" = _UserID AND "ClientKey" = _ClientKey),
		"ModifiedBy" = _UserID,
		"ModifiedUserName" = (SELECT distinct "UserName" FROM connecthub."UserInfo" WHERE "UserKey" = _UserID AND "ClientKey" = _ClientKey),
		"IsReferedBy" = coalesce(_IsReferedBy,false),
		"Caste" = _Caste
