CREATE OR REPLACE FUNCTION connecthub."ProcSearchRequestorInfo"(_clientkey integer, _lang character, _userid character varying, _filtertype character varying, _ackey integer, _mandalkey integer, _villagekey integer, _value text, _searchpattern text)
 RETURNS json
 LANGUAGE plpgsql
AS $function$
DECLARE
    -- Variable declarations
    _return json;
    _List json;
    _locfilterstring text;
    _mlocfilterstring text;
    _Mobilefilter text;
    _mMobilefilter text;
    _Namefilter text;
    _mNamefilter text;
    _sort text;
    _query text;

    -- Exception variables
    _state   TEXT;
    _msg     TEXT;
    _detail  TEXT;
    _hint    TEXT;
    _context TEXT;

BEGIN
    -- Location filter logic
    IF _ACKey IS NULL AND _MandalKey IS NULL AND _VillageKey IS NULL THEN 
        _locfilterstring = '';
        _mlocfilterstring = '';
    ELSIF _ACKey IS NOT NULL AND _MandalKey IS NULL AND _VillageKey IS NULL THEN
--        _locfilterstring = ' AND a."ACKey" = ' || _ACKey||' ';
        _locfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey||' ';
        _mlocfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey||' ';
    ELSIF _ACKey IS NOT NULL AND _MandalKey IS NOT NULL AND _VillageKey IS NULL THEN
--        _locfilterstring = ' AND a."ACKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey||' ';
	    _locfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey||' ';
        _mlocfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey||' ';
    ELSIF _ACKey IS NOT NULL AND _MandalKey IS NOT NULL AND _VillageKey IS NOT NULL THEN
--        _locfilterstring = ' AND a."ACKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey || ' AND "VillageKey" = ' || _VillageKey ||' ';
       _locfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey || ' AND "VillageKey" = ' || _VillageKey ||' ';
        _mlocfilterstring = ' AND a."ConstituencyKey" = ' || _ACKey || ' AND "MandalKey" = ' || _MandalKey || ' AND "VillageKey" = ' || _VillageKey ||' ';
    END IF;

    -- Filter type logic
    IF _FilterType = 'Mobile' THEN 
--	    _Mobilefilter = ' AND "Mobile" = ' || quote_literal(_Value);
	    _Mobilefilter = ' AND (trim("Mobile") = ' || quote_literal(_Value) || ' OR trim("AlternateMobile") = ' || quote_literal(_Value) || ')';
        _mMobilefilter = ' AND (trim("Mobile") = ' || quote_literal(_Value) || ' OR trim("AlternateMobile") = ' || quote_literal(_Value) || ')';
        _Namefilter = '';
        _mNamefilter = '';
        _sort = '';
    ELSIF _FilterType = 'Name' THEN 
        _Mobilefilter = '';
        _mMobilefilter = '';
        _Namefilter = 'AND (last_name_first_name_cleansed ~*   ''' || coalesce(_SearchPattern, _Value) || '''  
            OR first_name_last_name_cleansed ~*  ''' || coalesce(_SearchPattern, _Value) || ''' )';
--            OR "First_Name" ||  "Last_Name" ~* (' || quote_literal(coalesce(_SearchPattern, _Value)) || ' || ''$''))';
        _mNamefilter = 'AND (last_name_first_name_cleansed ~*   ''' || coalesce(_SearchPattern, _Value) || '''  
            OR first_name_last_name_cleansed ~*  ''' || coalesce(_SearchPattern, _Value) || ''' )';
--            OR "RequestorFirstName" || "RequestorLastName" ~* (' || quote_literal(coalesce(_SearchPattern, _Value)) || ' || ''$''))';
        _sort = 'order by 
				case 
				when last_name_first_name_cleansed ~* ''^'||coalesce(replace(_SearchPattern,',*?',''), _Value)||'$'' 
				then 1  -- Exact match ( with spell correction )
				when first_name_last_name_cleansed ~* ''^'||coalesce(replace(_SearchPattern,',*?',''), _Value)||'$'' 
				then 1 -- Exact match ( with spell correction )
				when last_name_first_name_cleansed ~* ''^'||coalesce(replace(_SearchPattern,',*?',''), _Value)||'''
				then 2 -- word1Word2 % ( with spell correction )
				when first_name_last_name_cleansed ~* ''^'||coalesce(replace(_SearchPattern,',*?',''), _Value)||'''
				then 2 -- word1Word2 % ( with spell correction )
				when last_name_first_name_cleansed ~* '''||coalesce(replace(_SearchPattern,',*?',''), _Value)||'$'' 
				then 3 -- % word1Word2 ( with spell correction )
				when first_name_last_name_cleansed ~* '''||coalesce(replace(_SearchPattern,',*?',''), _Value)||'$'' 
				then 3 -- % word1Word2 ( with spell correction )
				when last_name_first_name_cleansed ~* ''^'||coalesce(_SearchPattern, _Value)||'$'' 
				then 4 -- word1 % Word2 ( with spell correction )
				when first_name_last_name_cleansed ~* ''^'||coalesce(_SearchPattern, _Value)||'$'' 
				then 4 -- word1 % Word2 ( with spell correction )
				when last_name_first_name_cleansed ~* ''^'||coalesce(_SearchPattern, _Value)||'''
				then 5 -- word1 % Word2 % ( with spell correction )
				when first_name_last_name_cleansed ~* ''^'||coalesce(_SearchPattern, _Value)||'''
				then 5 -- word1 % Word2 % ( with spell correction )
				when last_name_first_name_cleansed ~* '''||coalesce(_SearchPattern, _Value)||'$'' 
				then 6 -- % word1 % Word2 ( with spell correction )
				when first_name_last_name_cleansed ~* '''||coalesce(_SearchPattern, _Value)||'$''
				then 6 -- % word1 % Word2 ( with spell correction )
				when last_name_first_name_cleansed ~* '''||coalesce(_SearchPattern, _Value)||''' 
				then 7 -- % word1 % Word2 % ( with spell correction )
				when first_name_last_name_cleansed ~* '''||coalesce(_SearchPattern, _Value)||'''
				then 7 -- % word1 % Word2 % ( with spell correction )
				else 8
				end';
--        	'ORDER BY 
--                    similarity("RequestorFirstName" ||  "RequestorLastName", ' || quote_literal(_Value) || ') DESC,
--                    CASE 
--                        WHEN "RequestorFirstName" ||  "RequestorLastName" ~* (''^'' || ' || quote_literal(coalesce(_SearchPattern, _Value)) || ' || ''$'') THEN 1
--				WHEN "RequestorLastName" ||  "RequestorFirstName" ~* (''^'' || ' || quote_literal(coalesce(_SearchPattern, _Value)) || ' || ''$'') THEN 1
--                        WHEN "RequestorFirstName" ||  "RequestorLastName" ~* (''^'' || ' || quote_literal(coalesce(_SearchPattern, _Value)) || ' ) THEN 2
--				WHEN "RequestorLastName" ||  "RequestorFirstName" ~* (''^'' || ' || quote_literal(coalesce(_SearchPattern, _Value)) || ' ) THEN 2
--                        WHEN "RequestorFirstName" ||  "RequestorLastName" ~* (' || quote_literal(coalesce(_SearchPattern, _Value)) || ' || ''$'') THEN 3
--				WHEN "RequestorLastName" ||  "RequestorFirstName" ~* (' || quote_literal(coalesce(_SearchPattern, _Value)) || ' || ''$'') THEN 3
--                        ELSE 4 
--                    END, 
--                    LENGTH("RequestorFirstName" || "RequestorLastName")';
    END IF;

    -- Main query construction
    _query = 'SELECT json_agg(row_to_json(a)) 
                FROM (
                    SELECT "VerificationStatus",
					 "Source","RequestorID","Photo","RequestorType","RequestorTypeName",
                    "RequestorFirstName","RequestorLastName","Age","Gender","Mobile",
                    "AlternateMobile","Email","OccupationID","Occupation","Union",
                    "UnionInfo","ConstituencyType","ConstituencyKey","ConstituencyName",
                    "MandalKey","Mandal","VillageKey","Village","Door/FlatNo" door_flat_no,"PinCode",
                    "AddressLine1","AddressLine2","RelationType","RelationName",
                    "RelationContact","RelationAlternateContact","Tags"::json,
                    "AdditionalInfo"::json,"IsActive","CreatedBy","CreatedOn","ModifiedBY","ModifiedOn",first_name_last_name_cleansed,last_name_first_name_cleansed, "VoterID","Caste"
					 FROM (
					select  ''Not Verified'' "VerificationStatus",
							"Source","RequestorID","Photo","RequestorType","RequestorTypeName","RequestorFirstName",
							"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email","OccupationID","Occupation",
							"Union","UnionInfo"::text,"ConstituencyType","ConstituencyKey","ConstituencyName","MandalKey","Mandal",
							"VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2","RelationType",
							"RelationName","RelationContact","RelationAlternateContact",
							(case when json_agg(case when t."ID" = 0 then ''{}'' else json_build_object(''ID'', t."ID", ''Value'', t."Tags") end )::text = ''[{}]'' then ''[]''::json 
							else json_agg(case when t."ID" = 0 then ''{}'' else json_build_object(''ID'', t."ID", ''Value'', t."Tags") end ) end)::text "Tags",
							coalesce("AdditionalInfo"::text::json, ''[]''::json)::text "AdditionalInfo",
							a."IsActive",a."CreatedBy",a."CreatedOn",a."ModifiedBY",a."ModifiedOn"
							, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed,"VoterID", "Caste"
							from 
							(select ''voter'' "Source",
							"RequestorID","Photo","RequestorType","RequestorTypeName","RequestorFirstName",
							"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email",a."Occupation" as "OccupationID",
							o."Occupation",
							"Union","UnionInfo","ConstituencyType","ConstituencyKey","ConstituencyName","MandalKey",
							"Mandal","VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2",
							"RelationType","RelationName","RelationContact","RelationAlternateContact",unnest("Tags") "TagID",
							"AdditionalInfo",a."IsActive","CreatedUserName" "CreatedBy",a."CreatedOn",
							"ModifiedUserName" "ModifiedBY",a."ModifiedOn","IsReferedBy", a."ClientKey", a."Lang","VoterID"
							, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed, a."Caste"
							from  hubviews."VotersInfo" 
							as a 
							left join masters."OccupationList" as o on o."ClientKey" = a."ClientKey" and o."Lang" = a."Lang" and o."ID" = a."Occupation"
							) as a
							left join masters."TagList" as t on t."ClientKey" = a."ClientKey" and t."Lang" = a."Lang" and a."TagID" = t."ID"::text
							WHERE a."ClientKey" = '||_ClientKey||' AND a."Lang" = '||quote_literal(_Lang)||' 
                          AND "VoterID" NOT IN (SELECT "VoterID" FROM connecthub."RequestorInformation" WHERE "VoterID" IS NOT NULL)
                          ' || _locfilterstring || _Mobilefilter || _Namefilter || '
							group by "Source","RequestorID","Photo","RequestorType","RequestorTypeName","RequestorFirstName",
							"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email","OccupationID","Occupation",
							"Union","UnionInfo"::text,"ConstituencyType","ConstituencyKey","ConstituencyName","MandalKey","Mandal",
							"VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2","RelationType",
							"RelationName","RelationContact","RelationAlternateContact","AdditionalInfo"::text,
							a."IsActive",a."CreatedBy",a."CreatedOn",a."ModifiedBY",a."ModifiedOn"
							, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed,"VoterID", a."Caste"
                    UNION
--                      
						select ''Verified'' "VerificationStatus",
							"Source","RequestorID"::text,"Photo","RequestorType","RequestorTypeName","RequestorFirstName",
							"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email","OccupationID","Occupation",
							"Union","UnionInfo"::text,"ConstituencyType","ConstituencyKey","ConstituencyName","MandalKey","Mandal",
							"VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2","RelationType",
							"RelationName","RelationContact","RelationAlternateContact",
							(case when json_agg(case when t."ID" = 0 then ''{}'' else json_build_object(''ID'', t."ID", ''Value'', t."Tags") end )::text = ''[{}]'' then ''[]''::json 
							else json_agg(case when t."ID" = 0 then ''{}'' else json_build_object(''ID'', t."ID", ''Value'', t."Tags") end ) end)::text "Tags",
							coalesce("AdditionalInfo"::text::json, ''[]''::json)::text "AdditionalInfo",
							a."IsActive",a."CreatedBy",a."CreatedOn",a."ModifiedBY",a."ModifiedOn"
							, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed, "VoterID", a."Caste"
							from 
							(select ''meenestham'' "Source",
							"RequestorID","Photo","RequestorType","RequestorTypeName","RequestorFirstName",
							"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email",a."Occupation" as "OccupationID",
