							"Mandal","VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2",
							"RelationType","RelationName","RelationContact","RelationAlternateContact",unnest("Tags") "TagID",
							"AdditionalInfo",a."IsActive","CreatedUserName" "CreatedBy",a."CreatedOn",
							"ModifiedUserName" "ModifiedBY",a."ModifiedOn","IsReferedBy", a."ClientKey", a."Lang","VoterID"
							, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed, a."Caste"
							from  connecthub."RequestorInformation" 
							as a 
							left join masters."OccupationList" as o on o."ClientKey" = a."ClientKey" and o."Lang" = a."Lang" and o."ID" = a."Occupation"
							) as a
							left join masters."TagList" as t on t."ClientKey" = a."ClientKey" and t."Lang" = a."Lang" and a."TagID" = t."ID"::text
							 WHERE a."ClientKey" = '||_ClientKey||' AND a."Lang" = '||quote_literal(_Lang)||' 
                          ' || _mlocfilterstring || _mMobilefilter || _mNamefilter || '
							group by "Source","RequestorID","Photo","RequestorType","RequestorTypeName","RequestorFirstName",
							"RequestorLastName","Age","Gender","Mobile","AlternateMobile","Email","OccupationID","Occupation",
							"Union","UnionInfo"::text,"ConstituencyType","ConstituencyKey","ConstituencyName","MandalKey","Mandal",
							"VillageKey","Village","Door/FlatNo","PinCode","AddressLine1","AddressLine2","RelationType",
							"RelationName","RelationContact","RelationAlternateContact","AdditionalInfo"::text,
							a."IsActive",a."CreatedBy",a."CreatedOn",a."ModifiedBY",a."ModifiedOn"
							, a.first_name_last_name_cleansed, a.last_name_first_name_cleansed, "VoterID", a."Caste"
                    ) AS innerquery
                    ' || _sort || '
                ) AS a';

    	RAISE NOTICE '%', _query;
   
	   	execute _query into _List;

    _return = json_build_object('Status', 'Success', 'Details', json_build_object('List', _List));
   
    RETURN _return;

EXCEPTION WHEN OTHERS THEN
    GET STACKED DIAGNOSTICS
        _state = returned_sqlstate,
        _msg = message_text,
        _detail = pg_exception_detail,
        _hint = pg_exception_hint,
        _context = pg_exception_context;

    INSERT INTO connecthub."EXCEPTION_LOG" ("procedure", "state", "msg", "detail", "hint", "context")
    VALUES ('ProcSearchRequestorInfo', _state, _msg, _detail, _hint, _context);

    _return = json_build_object('Status', 'Failed', 'Details', json_build_object('State', _state, '_msg', _msg, '_detail', _detail, '_hint', _hint, '_context', _context));
    RETURN _return;
END;
$function$
